import React from "react";
import { useDispatch } from "react-redux";
import { setSort } from "../../redux/filter/slice";
import { Sort, SortPropertyEnum } from "../../redux/filter/types";


type OptionsItem = {
  name: string;
  sortProperty: SortPropertyEnum;
};

type SortPopupProps = {
  sort: Sort;
};

export const options: OptionsItem[] = [
  { name: "популярністю(DESC)", sortProperty: SortPropertyEnum.PRICE_DESC },
  { name: "популярністю(ASC)", sortProperty: SortPropertyEnum.PRICE_ASC },
  { name: "вартістю(DESC)", sortProperty: SortPropertyEnum.PRICE_DESC },
  { name: "вартістю(ASC)", sortProperty: SortPropertyEnum.PRICE_ASC },
  { name: "абеткою(DESC)", sortProperty: SortPropertyEnum.TITLE_DESC },
  { name: "абеткою(ASC)", sortProperty: SortPropertyEnum.TITLE_ASC },
];

const SortPopup: React.FC<SortPopupProps> = ({ sort }) => {
  const dispatch = useDispatch();
  const sortRef = React.useRef<HTMLDivElement>(null);

  const [Open, makeOpen] = React.useState(false);

  const makeSelectedAndClose = (obj: OptionsItem) => {
    dispatch(setSort(obj));
    makeOpen(false);
  };


  type PopupClick = MouseEvent & {
    path: Node[];
  };

  React.useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      const _event = event as PopupClick;
      if (sortRef.current && !_event.path.includes(sortRef.current)) {
        makeOpen(false);
      }
    };

    document.body.addEventListener("click", handleClickOutside);
    return () => document.body.removeEventListener("click", handleClickOutside);
  }, []);
  return (
    <div className="sort" ref={sortRef}>
      <div className="sort__label">
        <svg
          width="10"
          height="6"
          viewBox="0 0 10 6"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
        >
          <path
            d="M10 5C10 5.16927 9.93815 5.31576 9.81445 5.43945C9.69075 5.56315 9.54427 5.625 9.375 5.625H0.625C0.455729 5.625 0.309245 5.56315 0.185547 5.43945C0.061849 5.31576 0 5.16927 0 5C0 4.83073 0.061849 4.68424 0.185547 4.56055L4.56055 0.185547C4.68424 0.061849 4.83073 0 5 0C5.16927 0 5.31576 0.061849 5.43945 0.185547L9.81445 4.56055C9.93815 4.68424 10 4.83073 10 5Z"
            fill="#2C2C2C"
          />
        </svg>
        <b>Сортувати за:</b>
        <span onClick={() => makeOpen(!Open)}>{sort.name}</span>
      </div>
      {Open && (
        <div className="sort__popup">
          <ul>
            {options.map((option, index) => (
              <li
                key={`${option}_${index}`}
                onClick={() => makeSelectedAndClose(option)}
                className={
                  sort.sortProperty === option.sortProperty ? "active" : ""
                }
              >
                {option.name}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};
export default SortPopup;
